import { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

function ScrollTopButton() {
  const [displayText, setDisplayText] = useState("Scroll");

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const windowBottom = scrollY + window.innerHeight;
      const documentHeight = document.body.scrollHeight;

      if (scrollY < 600) {
        setDisplayText("Scroll");
      } else if (windowBottom >= documentHeight * 0.8) {
        setDisplayText("Top");
      } else {
        setDisplayText("");
      }
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll();

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleClick = () => {
    if (displayText === "Top") {
      window.scrollTo({ top: 0, behavior: "smooth" });
    }
  };

  return (
    <AnimatePresence>
      {displayText && (
        <motion.div
          key={displayText}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: 20 }}
          transition={{ duration: 0.3 }}
          className="fixed bottom-6 left-6 z-10 bg-white px-3 py-1 rounded shadow cursor-pointer text-black"
          onClick={handleClick}
        >
          {displayText}
        </motion.div>
      )}
    </AnimatePresence>
  );
}
