import React, { useState, useEffect, useRef } from "react";
import { gsap } from "gsap";

function LoadingScreen({ isLoading, onFadeOutComplete }) {
  const loaderRef = useRef(null);

  useEffect(() => {
    // 先淡入
    gsap.fromTo(
      loaderRef.current,
      { opacity: 0 },
      { opacity: 1, duration: 1, ease: "power2.out" }
    );
  }, []);

  useEffect(() => {
    if (!isLoading) {
      // 當父元件告訴 loading 狀態 false，才淡出
      gsap.to(loaderRef.current, {
        opacity: 0,
        duration: 1,
        ease: "power2.in",
        onComplete: onFadeOutComplete,
      });
    }
  }, [isLoading, onFadeOutComplete]);

  return (
    <div
      ref={loaderRef}
      className="fixed inset-0 flex items-center justify-center bg-default z-50"
    >
      <img
        src="../assets/images/van.webp"
        alt="nunu loading"
        className="w-60 h-auto aspect-auto object-contain"
      />
    </div>
  );
}

export default LoadingScreen;
