import React, { useRef, useEffect } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

export default function MapScroll() {
  const containerRef = useRef(null);
  const contentRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;
    const content = contentRef.current;
    const totalScroll = content.scrollWidth - window.innerWidth;

    const ctx = gsap.context(() => {
      gsap.to(content, {
        x: `-${totalScroll}px`,
        ease: "none",
        scrollTrigger: {
          trigger: container,
          start: "top top",
          end: `+=${totalScroll}`,
          scrub: true,
          pin: true,
          anticipatePin: 1,
          onUpdate: (self) => {
            console.log("scrolling...", self.progress.toFixed(2));
          },
        },
      });
    }, containerRef);

    ScrollTrigger.refresh();
    return () => ctx.revert();
  }, []);

  return (
    <section ref={containerRef} className="relative h-[100vh] overflow-hidden">
      <div ref={contentRef} className="flex w-fit h-full">
        {["#f97316", "#10b981", "#3b82f6", "#f43f5e", "#facc15"].map(
          (color, i) => (
            <div
              key={i}
              className="w-screen h-screen flex items-center justify-center text-white text-4xl font-bold"
              style={{ backgroundColor: color }}
            >
              Section {i + 1}
            </div>
          )
        )}
      </div>
    </section>
  );
}
