import React, { useRef, useState, useEffect, useMemo } from "react";
import { motion, useAnimation, AnimatePresence } from "framer-motion";
import van1 from "../assets/images/van1.webp";
import van2 from "../assets/images/van2.webp";
import marker1 from "../assets/images/cake/mainImage.webp";
import marker2 from "../assets/images/cake/mainImage.webp";

// 輔助函數：生成指定範圍內的隨機整數
const getRandomInt = (min, max) => {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min + 1)) + min;
};

// 新增函數：生成方正的隨機路徑
const generateSquaredPath = (width, height, numSegments = 5) => {
  let pathD = "";
  let currentX = getRandomInt(width * 0.1, width * 0.2); // 起始X靠近左側
  let currentY = getRandomInt(height * 0.8, height * 0.9); // 起始Y靠近底部

  pathD += `M${currentX},${currentY}`; // 移動到起始點

  for (let i = 0; i < numSegments; i++) {
    // 隨機決定是水平移動還是垂直移動
    const direction = Math.random() < 0.5 ? "horizontal" : "vertical";
    const segmentLength = getRandomInt(50, 150); // 每段長度

    if (direction === "horizontal") {
      // 水平移動，方向隨機向左或向右
      const newX = currentX + (Math.random() < 0.5 ? -1 : 1) * segmentLength;
      currentX = Math.max(20, Math.min(width - 20, newX)); // 確保在邊界內
      pathD += `H${currentX}`;
    } else {
      // 垂直移動，方向隨機向上或向下
      const newY = currentY + (Math.random() < 0.5 ? -1 : 1) * segmentLength;
      currentY = Math.max(20, Math.min(height - 20, newY)); // 確保在邊界內
      pathD += `V${currentY}`;
    }

    // 每隔一段隨機加入一個L (line to) 指令來稍微「偏離」主軸，增加多樣性
    if (i > 0 && Math.random() < 0.3) {
      const nextX = getRandomInt(
        Math.max(20, currentX - 50),
        Math.min(width - 20, currentX + 50)
      );
      const nextY = getRandomInt(
        Math.max(20, currentY - 50),
        Math.min(height - 20, currentY + 50)
      );
      pathD += `L${nextX},${nextY}`;
      currentX = nextX;
      currentY = nextY;
    }
  }

  // 確保終點在合理範圍內，並可作為標記點
  // 我們可以選擇讓最後一個點位於相對固定的區域，例如右上角
  const finalX = getRandomInt(width * 0.7, width * 0.9);
  const finalY = getRandomInt(height * 0.1, height * 0.3);
  pathD += `L${finalX},${finalY}`; // 強制連接到一個終點

  return pathD;
};

export default function MapScene() {
  const svgWidth = 650;
  const svgHeight = 200;

  const pathA = useRef(null);
  const pathB = useRef(null);
  const controls = useAnimation();

  const [currentLoc, setCurrentLoc] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isMoving, setIsMoving] = useState(false);
  const [carPos, setCarPos] = useState({ x: 0, y: 0 });
  const [carImg, setCarImg] = useState(van1);

  // 使用 useMemo 來生成方正的隨機路徑
  const randomPathA = useMemo(
    () => generateSquaredPath(svgWidth, svgHeight, 7),
    [svgWidth, svgHeight]
  );
  const randomPathB = useMemo(
    () => generateSquaredPath(svgWidth, svgHeight, 7),
    [svgWidth, svgHeight]
  ); // 增加路徑段數

  // 更新 locations 陣列，使其使用隨機生成的路徑
  const locations = useMemo(() => {
    return [
      {
        id: 1,
        pathId: "pathA",
        markerImg: marker1,
        modal: {
          name: "nunu Shop - 隨機路線 1",
          address: "新加坡隨機地址 A",
          hours: "Mon - Sun | 10:00 - 18:00",
        },
        carImg: van1,
      },
      {
        id: 2,
        pathId: "pathB",
        markerImg: marker2,
        modal: {
          name: "nunu Van - 隨機路線 2",
          address: "新加坡隨機地址 B",
          hours: "Weekend | 11:00 - 18:00",
        },
        carImg: van2,
      },
    ];
  }, []);

  // on load, 將車子放在隨機選取的一個路徑起點
  useEffect(() => {
    // 確保 pathA.current 和 pathB.current 已經被賦值
    if (pathA.current && pathB.current) {
      const initialPathRef = Math.random() < 0.5 ? pathA : pathB;
      const p = initialPathRef.current.getPointAtLength(0);
      setCarPos({ x: p.x, y: p.y });
      // 設定初始車輛圖片為第一個地點的車輛圖片 (或隨機選擇一個)
      setCarImg(locations[0].carImg);
    }
  }, [locations, randomPathA, randomPathB]); // 依賴於 randomPathA/B 確保在路徑更新後重新設定起點

  // 將車子位置綁到 controls
  useEffect(() => {
    controls.set({ x: carPos.x, y: carPos.y });
  }, [carPos, controls]);

  const moveAlong = async (pathRef) => {
    setIsMoving(true);
    const pathEl = pathRef.current;
    if (!pathEl) {
      setIsMoving(false);
      return;
    }
    const total = pathEl.getTotalLength();

    await new Promise((res) => {
      let t = 0;
      const step = () => {
        // 加快移動速度
        t += 0.01; // 從 0.005 調整為 0.01
        if (t >= 1) t = 1;
        const p = pathEl.getPointAtLength(total * t);
        controls.set({ x: p.x, y: p.y });
        if (t < 1) {
          requestAnimationFrame(step);
        } else {
          res();
        }
      };
      requestAnimationFrame(step);
    });

    setTimeout(() => {
      setShowModal(true);
      setIsMoving(false);
    }, 1000);
  };

  const onMarkerClick = (loc) => {
    if (isMoving) return;
    setShowModal(false);
    setCurrentLoc(loc);
    setCarImg(loc.carImg);
    const ref = loc.pathId === "pathA" ? pathA : pathB;
    moveAlong(ref);
  };

  return (
    <div
      className="relative w-full h-[400px] overflow-hidden overflow-x-auto"
      style={{ height: `${svgHeight}px` }}
    >
      <svg
        className="absolute w-full h-full"
        viewBox={`0 0 ${svgWidth} ${svgHeight}`}
      >
        {/* pathA: 隨機方正路徑 1 */}
        <path
          ref={pathA}
          d="M0 89H227.5V4H337"
          fill="none"
          stroke="#3B3B3B"
          strokeOpacity="0.63"
          strokeWidth="8"
          strokeLinejoin="round"
          strokeDasharray="5 5"
        />

        {/* pathB: 點1 → 點2（右邊，已整體右移） */}
        <path
          ref={pathB}
          d="M350 10V209H413V26H491.5"
          fill="none"
          stroke="#3B3B3B"
          strokeOpacity="0.63"
          strokeWidth="8"
          strokeLinejoin="round"
          strokeDasharray="5 5"
        />
      </svg>

      {/* 小車 */}
      <motion.img
        src={carImg}
        alt="van"
        className="absolute w-36 h-36 pointer-events-none"
        animate={controls}
        transition={{ ease: "linear" }}
      />

      {/* Markers */}
      {locations.map((loc) => {
        const ref = loc.pathId === "pathA" ? pathA : pathB;
        // 確保 ref.current 存在
        const total = ref.current?.getTotalLength() || 0;
        const p = ref.current?.getPointAtLength(total) || { x: 0, y: 0 };
        return (
          <img
            key={loc.id}
            src={loc.markerImg}
            alt={`marker${loc.id}`}
            className="absolute w-20 h-20 cursor-pointer"
            style={{ left: p.x - 2, top: p.y - 0 }}
            onClick={() => onMarkerClick(loc)}
          />
        );
      })}

      {/* Modal */}
      <AnimatePresence>
        {showModal && currentLoc && (
          <motion.div
            className="fixed inset-0 bg-black/50 flex items-center justify-center z-10"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <motion.div
              className="relative bg-white rounded-2xl p-6 shadow-2xl max-w-sm w-full border-4 border-orange-400"
              initial={{ scaleX: 0 }}
              animate={{ scaleX: 1 }}
              exit={{ scaleX: 0 }}
              transition={{ duration: 0.4 }}
              style={{ transformOrigin: "center" }}
            >
              <button
                className="absolute top-3 right-3 text-2xl text-gray-500 hover:text-gray-800"
                onClick={() => setShowModal(false)}
              >
                &times;
              </button>
              <h2 className="text-2xl font-bold text-orange-600 mb-2">
                {currentLoc.modal.name}
              </h2>
              <p className="text-gray-700 mb-1">
                <span className="font-semibold">地址：</span>
                {currentLoc.modal.address}
              </p>
              <p className="text-gray-700">
                <span className="font-semibold">營業時間：</span>
                {currentLoc.modal.hours}
              </p>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
