import React, { useState } from "react";
import oreocake from "../assets/images/cake/oreo.png";
import oreocakeTitle from "../assets/images/cake/oreo-title.png";
import tarocake from "../assets/images/cake/taro.png";
import tarocakeTitle from "../assets/images/cake/taro-title.png";
import redbeancake from "../assets/images/cake/redbean.png";
import redbeancakeTitle from "../assets/images/cake/redbean-title.png";
import vanillacake from "../assets/images/cake/vanilla.png";
import vanillacakeTitle from "../assets/images/cake/vanilla-title.png";

// 每個 tab 定義不同文字位置 layout
const tabs = [
  {
    id: "tab1",
    labelImage: oreocake,
    labelTitleImage: oreocakeTitle,
    content: {
      image: oreocake,
      textList: [
        "Real Oreo cookie bits inside",
        "Lightly sweet with a chocolatey note",
        "A flavor loved by kids",
      ],
      layout: [
        { top: "0%", left: "-10%" },
        { top: "0%", left: "40%" },
        { top: "70%", left: "55%" },
      ],
    },
  },
  {
    id: "tab2",
    labelImage: tarocake,
    labelTitleImage: tarocakeTitle,
    content: {
      image: tarocake,
      textList: [
        "A soft mix of sweet and savory",
        "Premium taro from Dajia, Taiwan",
        "Nature taro taste",
      ],
      layout: [
        { top: "15%", left: "15%" },
        { top: "50%", left: "5%" },
        { top: "75%", left: "20%" },
      ],
    },
  },
  {
    id: "tab3",
    labelImage: redbeancake,
    labelTitleImage: redbeancakeTitle,
    content: {
      image: redbeancake,
      textList: [
        "Slow-cooked for a soft, gently textured bite",
        "Made with red beans grown in Taiwan",
        "Comforting classic that never goes out of style",
      ],
      layout: [
        { top: "20%", left: "55%" },
        { top: "50%", left: "30%" },
        { top: "75%", left: "60%" },
      ],
    },
  },
  {
    id: "tab4",
    labelImage: vanillacake,
    labelTitleImage: vanillacakeTitle,
    content: {
      image: vanillacake,
      textList: [
        "Velvety soft, melts right in your mouth",
        "Made with real milk and a hint of vanilla",
        "A gentle favorite for all ages",
      ],
      layout: [
        { top: "65%", left: "20%" },
        { top: "75%", left: "50%" },
        { top: "85%", left: "30%" },
      ],
    },
  },
];

const Tabs = () => {
  const [activeTab, setActiveTab] = useState(tabs[0].id);
  const currentContent = tabs.find((tab) => tab.id === activeTab)?.content;

  return (
    <div className="relative text-center px-6 md:px-20 mb-20">
      {currentContent && (
        <div className="relative flex justify-center items-center">
          <div className="relative w-[320px] md:w-[420px] xl:w-[500px]">
            {/* 中央圖片 */}
            <img
              className="w-full h-auto mx-auto"
              src={currentContent.image}
              alt="Cake"
            />
            {currentContent.textList.map((text, index) => {
              const layout = currentContent.layout[index];
              return (
                <div
                  key={index}
                  className="absolute text-xs md:text-base p-2 w-[180px] text-left leading-snug"
                  style={{
                    top: layout.top,
                    left: layout.left,
                  }}
                >
                  {text}
                </div>
              );
            })}
          </div>
        </div>
      )}

      {/* Tabs 標籤區 */}
      <div className="mt-12 flex justify-center items-center gap-8 flex-wrap">
        {tabs.map((tab) => (
          <div
            key={tab.id}
            onClick={() => setActiveTab(tab.id)}
            className={`cursor-pointer flex flex-col items-center gap-2 transition-all hover:scale-105 ${
              tab.id === activeTab ? "opacity-100" : "opacity-50"
            }`}
          >
            <img className="w-24 h-auto" src={tab.labelImage} alt="" />
            <img className="h-6" src={tab.labelTitleImage} alt="" />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tabs;
