import { useState } from "react";
import { motion } from "framer-motion";
import { X } from "lucide-react";

const positions = {
  shop: {
    left: "530px",
    top: "120px",
    label: "nunu Shop",
    desc: "266 South Bridge Rd, #000, Singapore",
    time: "Mon - Sun | 10:00 - 18:00",
    icon: "shop",
  },
  van: {
    left: "960px",
    top: "290px",
    label: "nunu Van",
    desc: "3 Temasek Blvd, Singapore 038983",
    time: "Weekend | 11:00 - 18:00",
    icon: "van",
  },
};

export default function MapScene() {
  const [modal, setModal] = useState(null);
  const [truckPos, setTruckPos] = useState({ left: "-50px", top: "60px" });
  const [isMoving, setIsMoving] = useState(false);

  const handleClick = (key) => {
    const target = positions[key];
    setIsMoving(true);
    setTruckPos({ left: target.left, top: target.top });
    setTimeout(() => {
      setIsMoving(false);
      setModal(key);
    }, 1200);
  };

  return (
    <div className="relative">
      {/* SVG Path */}
      <svg
        width="1280"
        height="425"
        viewBox="0 0 1280 425"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
        className="absolute top-0 left-0"
      >
        <path
          d="M-2 78.9951C-2 78.9951 490.5 -76.9943 551 63C611.5 202.994 370.5 292.5 530.5 389.5C690.5 486.5 1277.5 324 1277.5 324"
          stroke="#3B3B3B"
          strokeOpacity="0.63"
          strokeWidth="8"
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeDasharray="23 23"
        />
      </svg>

      {/* Location Buttons */}
      {Object.entries(positions).map(([key, pos]) => (
        <button
          key={key}
          className="absolute z-10"
          style={{ left: pos.left, top: pos.top }}
          onClick={() => handleClick(key)}
        >
          <img
            src={`/icons/${pos.icon}.png`}
            alt={pos.label}
            className="w-12"
          />
        </button>
      ))}

      {/* Truck */}
      <motion.img
        src="/icons/truck.png"
        alt="truck"
        initial={false}
        animate={{ left: truckPos.left, top: truckPos.top }}
        transition={{ duration: 1, ease: "easeInOut" }}
        className="absolute w-24 z-20"
        style={{ position: "absolute" }}
      />

      {/* Modal */}
      {modal && (
        <motion.div
          initial={{ scaleX: 0 }}
          animate={{ scaleX: 1 }}
          exit={{ scaleX: 0 }}
          transition={{ duration: 0.5 }}
          className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 origin-center bg-white border-2 border-red-500 rounded-lg shadow-xl p-6 w-[300px]"
        >
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg font-bold">{positions[modal].label}</h3>
            <button onClick={() => setModal(null)}>
              <X className="w-5 h-5 text-red-500" />
            </button>
          </div>
          <img
            src={`/icons/${positions[modal].icon}.png`}
            alt="icon"
            className="w-16 h-16 mx-auto mb-2"
          />
          <p className="text-center text-sm text-gray-700 mb-1">
            {positions[modal].desc}
          </p>
          <p className="text-center text-xs text-gray-500">
            {positions[modal].time}
          </p>
        </motion.div>
      )}
    </div>
  );
}
