import { useRef, useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import carImg from "../assets/images/van1.webp";
import markerImg1 from "../assets/images/cake/mainImage.webp";
import markerImg2 from "../assets/images/cake/mainImage.webp";

export default function MapScene({}) {
  const pathA = useRef(null);
  const pathB = useRef(null);
  const [carPos, setCarPos] = useState({ x: 0, y: 0 });
  const [showCar, setShowCar] = useState(true);
  const [modalInfo, setModalInfo] = useState(null);

  const modalData = {
    1: {
      title: "nunu Shop",
      address: "266 South Bridge Rd, #000, Singapore",
      time: "Mon - Sun | 10:00 - 18:00",
      icon: markerImg1,
    },
    2: {
      title: "nunu Van",
      address: "3 Temasek Blvd, Singapore 038983",
      time: "Weekend | 11:00 - 18:00",
      icon: markerImg2,
    },
  };

  useEffect(() => {
    if (pathA.current) {
      const p = pathA.current.getPointAtLength(0);
      setCarPos({ x: p.x, y: p.y });
    }
  }, []);

  const runPath = (pathRef, onComplete) => {
    if (!pathRef.current) {
      console.error("Path not ready");
      return;
    }

    const total = pathRef.current.getTotalLength();
    let start = null;
    const duration = 1500;

    const animate = (timestamp) => {
      if (!start) start = timestamp;
      const elapsed = timestamp - start;
      const progress = Math.min(elapsed / duration, 1);
      const point = pathRef.current.getPointAtLength(total * progress);
      setCarPos({ x: point.x - 64, y: point.y - 64 });

      if (progress < 1) {
        requestAnimationFrame(animate);
      } else {
        setTimeout(() => {
          onComplete();
        }, 1000);
      }
    };

    requestAnimationFrame(animate);
  };

  const handleClick = (target) => {
    setShowCar(true);
    const pathRef = target === 1 ? pathA : pathB;
    runPath(pathRef, () => setModalInfo(modalData[target]));
  };

  const marker = (ref, targetId, imgSrc, ratio = 1) => {
    if (!ref.current) return null;
    const total = ref.current.getTotalLength();
    const p = ref.current.getPointAtLength(total * ratio);
    if (!p) return null;

    return (
      <img
        src={imgSrc}
        alt={`marker-${targetId}`}
        className="absolute w-32 h-32 cursor-pointer z-20" // 放大為 48x48px，z-index 增加
        style={{ left: p.x, top: p.y }}
        onClick={() => handleClick(targetId)}
      />
    );
  };

  return (
    <div className="relative w-full h-[425px] overflow-hidden">
      <svg
        className="absolute w-full h-full"
        viewBox="0 0 1280 425"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          ref={pathA}
          d="M-2 78.9951C-2 78.9951 490.5 -76.9943 551 63C611.5 202.994 370.5 292.5 530.5 389.5C690.5 486.5 1277.5 324 1277.5 324"
          stroke="#3B3B3B"
          strokeOpacity="0.63"
          strokeWidth="8"
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeDasharray="23 23"
        />
      </svg>
      {showCar && (
        <motion.img
          src={carImg}
          alt="car"
          className="absolute w-32 h-32"
          style={{ left: carPos.x, top: carPos.y }}
        />
      )}
      {marker(pathA, 1, markerImg1, 0.3)}
      {marker(pathA, 2, markerImg2, 0.75)}
      <AnimatePresence>
        {modalInfo && (
          <motion.div
            initial={{ scaleX: 0 }}
            animate={{ scaleX: 1 }}
            exit={{ scaleX: 0 }}
            transition={{ duration: 0.4 }}
            className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 origin-center bg-white border-2 border-red-500 rounded-lg shadow-xl p-6 w-[300px] z-30"
          >
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-bold">{modalInfo.title}</h3>
              <button onClick={() => setModalInfo(null)}>
                <span className="text-red-500 font-bold">×</span>
              </button>
            </div>
            <img
              src={modalInfo.icon}
              alt="modal icon"
              className="w-16 h-16 mx-auto mb-2"
            />
            <p className="text-center text-sm text-gray-700 mb-1">
              {modalInfo.address}
            </p>
            <p className="text-center text-xs text-gray-500">
              {modalInfo.time}
            </p>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
