import React, { useState, useEffect } from "react";
import { motion, useAnimation } from "framer-motion";

// 定義地點資料，包含座標、Modal 內容和車子方向
// Coordinates are approximate based on the provided image and for demonstration purposes.
// 您可以根據實際佈局調整 carX 和 carY 的值。
const locations = [
  {
    id: 1,
    name: "nunu Shop",
    address: "266 South Bridge Rd, #030, Singapore",
    hours: "Mon - Sun | 10:00 - 18:00",
    carX: 300, // 車子移動到的 X 座標
    carY: 100, // 車子移動到的 Y 座標
    direction: "right", // 車子面朝右
    pathD: "M 50 150 C 150 50, 250 50, 300 100", // 從起點到點1的曲線路徑 (示意)
  },
  {
    id: 2,
    name: "nunu Van",
    address: "Suntec City, 3 Temasek Blvd, Singapore 038983",
    hours: "Weekend | 11:00 - 18:00",
    carX: 100, // 車子移動到的 X 座標
    carY: 300, // 車子移動到的 Y 座標
    direction: "left", // 車子面朝左
    pathD: "M 300 100 C 250 150, 150 250, 100 300", // 從點1到點2的曲線路徑 (示意)
  },
];

// 車子 SVG 圖標
const CarSVG = ({ direction }) => (
  <svg
    className={`w-16 h-16 transition-transform duration-300 ${
      direction === "left" ? "scale-x-[-1]" : "" // 根據方向翻轉車子
    }`}
    viewBox="0 0 100 100"
    fill="none"
    xmlns="http://www.w3.org/2000/svg"
  >
    {/* 車身 */}
    <rect
      x="10"
      y="40"
      width="80"
      height="30"
      rx="8"
      fill="#F8D3A3"
      stroke="#D18D4D"
      strokeWidth="2"
    />
    {/* 車頂 */}
    <rect
      x="25"
      y="25"
      width="50"
      height="20"
      rx="5"
      fill="#F8D3A3"
      stroke="#D18D4D"
      strokeWidth="2"
    />
    {/* 窗戶 */}
    <rect
      x="30"
      y="30"
      width="15"
      height="10"
      rx="3"
      fill="#A0D8EF"
      stroke="#4FA8D8"
      strokeWidth="1"
    />
    <rect
      x="55"
      y="30"
      width="15"
      height="10"
      rx="3"
      fill="#A0D8EF"
      stroke="#4FA8D8"
      strokeWidth="1"
    />
    {/* 車輪 */}
    <circle cx="25" cy="70" r="8" fill="#333" stroke="#111" strokeWidth="2" />
    <circle cx="75" cy="70" r="8" fill="#333" stroke="#111" strokeWidth="2" />
    {/* 車輪餅圖標 (簡化) */}
    <circle
      cx="50"
      cy="50"
      r="10"
      fill="#FFC107"
      stroke="#E6A000"
      strokeWidth="2"
    />
    <text x="40" y="55" fontFamily="Arial" fontSize="15" fill="#8B4513">
      N
    </text>
  </svg>
);

const MapScene = () => {
  const [currentLocation, setCurrentLocation] = useState(locations[0]); // 初始位置為第一個點
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState(null);
  const [isAnimating, setIsAnimating] = useState(false); // 防止重複點擊
  const carControls = useAnimation(); // 控制車子動畫

  // 初始時將車子定位到第一個點
  useEffect(() => {
    carControls.set({ x: currentLocation.carX, y: currentLocation.carY });
  }, []); // 空依賴陣列表示只在組件掛載時執行一次

  const handlePointClick = async (location) => {
    if (isAnimating || currentLocation.id === location.id) {
      // 如果正在動畫中或已經在該點，則不執行任何操作
      return;
    }

    setIsAnimating(true); // 開始動畫，禁用點擊
    setIsModalOpen(false); // 關閉任何開啟的 Modal

    // 更新車子方向
    setCurrentLocation((prev) => ({ ...prev, direction: location.direction }));

    // 動畫車子到新位置
    await carControls.start({
      x: location.carX,
      y: location.carY,
      transition: { duration: 1.5, ease: "easeInOut" }, // 動畫時間可調整
    });

    // 更新當前位置
    setCurrentLocation(location);

    // 等待 1 秒後顯示 Modal
    setTimeout(() => {
      setModalContent(location);
      setIsModalOpen(true);
      setIsAnimating(false); // 動畫結束，啟用點擊
    }, 1000);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalContent(null);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4">
      <div className="max-w-4xl w-full">
        {/* 地圖區域 */}
        <div className="relative w-full h-96 overflow-hidden">
          {/* 虛線路徑 (簡化示意) */}
          <svg className="absolute w-full h-full" viewBox="0 0 1200 800">
            <path
              d="M930 0C930.241 0 930.478 0.0154335 930.711 0.0429688C1020.69 2.02232 1093 75.8057 1093 166.5C1093 258.438 1018.7 333 927 333C926.832 333 926.665 332.99 926.5 332.977C926.335 332.99 926.168 333 926 333H335C334.832 333 334.665 332.99 334.5 332.977C334.335 332.99 334.168 333 334 333C248.966 333 180 402.154 180 487.5C180 572.846 248.966 642 334 642C334.168 642 334.335 642.009 334.5 642.022C334.665 642.009 334.832 642 335 642H1274C1277.31 642 1280 644.686 1280 648C1280 651.314 1277.31 654 1274 654H335C334.832 654 334.665 653.99 334.5 653.977C334.335 653.99 334.168 654 334 654C242.303 654 168 579.438 168 487.5C168 395.562 242.303 321 334 321C334.168 321 334.335 321.009 334.5 321.022C334.665 321.009 334.832 321 335 321H926C926.168 321 926.335 321.009 926.5 321.022C926.665 321.009 926.832 321 927 321C1012.03 321 1081 251.846 1081 166.5C1081 81.1543 1012.03 12 927 12H6C2.68629 12 0 9.31371 0 6C0 2.68629 2.68629 0 6 0H930Z"
              fill="#3B3B3B"
              strokeDasharray="6 6"
              strokeWidth="1"
            />
          </svg>

          {/* 車子 */}
          <motion.div
            className="absolute"
            style={{ x: currentLocation.carX, y: currentLocation.carY }} // 初始定位
            animate={carControls} // 綁定動畫控制器
          >
            <CarSVG direction={currentLocation.direction} />
          </motion.div>

          {/* 地點標記 */}
          {locations.map((loc) => (
            <motion.div
              key={loc.id}
              className="absolute flex items-center justify-center w-12 h-12 bg-orange-500 text-white font-bold rounded-full cursor-pointer shadow-lg hover:bg-orange-600 transition-colors"
              style={{ left: loc.carX - 24, top: loc.carY - 24 }} // 調整位置使數字居中
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              onClick={() => handlePointClick(loc)}
              initial={{ opacity: 0, scale: 0 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ delay: 0.5 + loc.id * 0.2, duration: 0.5 }}
            >
              {loc.id}
            </motion.div>
          ))}
        </div>

        {/* Modal */}
        {isModalOpen && modalContent && (
          <motion.div
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <motion.div
              className="relative bg-white rounded-2xl p-8 shadow-2xl max-w-md w-full border-4 border-orange-400"
              initial={{ scaleX: 0, opacity: 0 }} // 從中心向左右展開
              animate={{ scaleX: 1, opacity: 1 }}
              exit={{ scaleX: 0, opacity: 0 }}
              transition={{ duration: 0.5, ease: "easeOut" }}
              style={{ transformOrigin: "center" }} // 確保從中心展開
            >
              <button
                onClick={closeModal}
                className="absolute top-4 right-4 text-gray-500 hover:text-gray-800 text-2xl font-bold transition-colors"
              >
                &times; {/* X 符號 */}
              </button>
              <h2 className="text-3xl font-bold text-orange-600 mb-4">
                {modalContent.name}
              </h2>
              <p className="text-gray-700 mb-2">
                <span className="font-semibold">地址:</span>{" "}
                {modalContent.address}
              </p>
              <p className="text-gray-700">
                <span className="font-semibold">營業時間:</span>{" "}
                {modalContent.hours}
              </p>
              {/* 您可以在這裡添加更多內容，例如地圖連結、電話等 */}
            </motion.div>
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default MapScene;
