import React, { useRef, useState, useEffect, useMemo } from "react";
import { motion, useAnimation, AnimatePresence } from "framer-motion";
import van1 from "../assets/images/van1.webp";
import van2 from "../assets/images/van2.webp";
import marker1 from "../assets/images/cake/mainImage.webp";
import marker2 from "../assets/images/cake/mainImage.webp";

// 輔助函數：生成指定範圍內的隨機整數
const getRandomInt = (min, max) => {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min + 1)) + min;
};

// 隨機生成 SVG path 的 'd' 屬性
const generateRandomPath = (width, height) => {
  // 設置幾個關鍵點的隨機範圍
  const startX = getRandomInt(50, width / 4);
  const startY = getRandomInt(height * 0.7, height - 50);

  const control1X = getRandomInt(width / 4, width / 2);
  const control1Y = getRandomInt(50, height / 2);

  const control2X = getRandomInt(width / 2, width * 0.75);
  const control2Y = getRandomInt(50, height / 2);

  const endX = getRandomInt(width * 0.75, width - 50);
  const endY = getRandomInt(50, height / 4);

  // Cubic Bezier Curve: M(start) C(control1, control2, end)
  return `M${startX},${startY} C${control1X},${control1Y} ${control2X},${control2Y} ${endX},${endY}`;
};

export default function MapScene() {
  const svgWidth = 600; // 增加 SVG 寬度以容納更廣的路徑
  const svgHeight = 400; // 增加 SVG 高度

  const pathA = useRef(null);
  const pathB = useRef(null);
  const controls = useAnimation();

  const [currentLoc, setCurrentLoc] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isMoving, setIsMoving] = useState(false);
  const [carPos, setCarPos] = useState({ x: 0, y: 0 });
  const [carImg, setCarImg] = useState(van1);

  // 使用 useMemo 來生成隨機路徑，確保它們在每次渲染時不會重複生成，除非 width/height 改變
  const randomPathA = useMemo(
    () => generateRandomPath(svgWidth, svgHeight),
    [svgWidth, svgHeight]
  );
  const randomPathB = useMemo(
    () => generateRandomPath(svgWidth, svgHeight),
    [svgWidth, svgHeight]
  );

  // 更新 locations 陣列，使其使用隨機生成的路徑
  const locations = useMemo(() => {
    return [
      {
        id: 1,
        pathId: "pathA",
        markerImg: marker1,
        modal: {
          name: "nunu Shop - 隨機路線 1",
          address: "新加坡隨機地址 A",
          hours: "Mon - Sun | 10:00 - 18:00",
        },
        carImg: van1,
      },
      {
        id: 2,
        pathId: "pathB",
        markerImg: marker2,
        modal: {
          name: "nunu Van - 隨機路線 2",
          address: "新加坡隨機地址 B",
          hours: "Weekend | 11:00 - 18:00",
        },
        carImg: van2,
      },
    ];
  }, []);

  // on load, 將車子放在隨機選取的一個路徑起點
  useEffect(() => {
    const initialPathRef = Math.random() < 0.5 ? pathA : pathB;
    const p = initialPathRef.current.getPointAtLength(0);
    setCarPos({ x: p.x, y: p.y });
    // 設定初始車輛圖片為第一個地點的車輛圖片
    setCarImg(locations[0].carImg);
  }, [locations]);

  // 將車子位置綁到 controls
  useEffect(() => {
    controls.set({ x: carPos.x, y: carPos.y });
  }, [carPos, controls]);

  const moveAlong = async (pathRef) => {
    setIsMoving(true);
    const pathEl = pathRef.current;
    if (!pathEl) {
      // 加一個檢查，確保 pathEl 存在
      setIsMoving(false);
      return;
    }
    const total = pathEl.getTotalLength();

    // 用 requestAnimationFrame 漸進更新進度
    await new Promise((res) => {
      let t = 0;
      const step = () => {
        t += 0.005;
        if (t >= 1) t = 1;
        const p = pathEl.getPointAtLength(total * t);
        controls.set({ x: p.x, y: p.y });
        if (t < 1) {
          requestAnimationFrame(step);
        } else {
          res();
        }
      };
      requestAnimationFrame(step);
    });

    // 停 1 秒後顯示 Modal
    setTimeout(() => {
      setShowModal(true);
      setIsMoving(false);
    }, 1000);
  };

  const onMarkerClick = (loc) => {
    if (isMoving) return;
    setShowModal(false);
    setCurrentLoc(loc);
    setCarImg(loc.carImg);
    // 選對路徑
    const ref = loc.pathId === "pathA" ? pathA : pathB;
    moveAlong(ref);
  };

  return (
    <div
      className="relative w-full h-[400px] bg-[#f1efea] overflow-hidden rounded-xl border"
      style={{ height: `${svgHeight}px` }} // 根據 SVG 高度調整容器高度
    >
      <svg
        className="absolute w-full h-full"
        viewBox={`0 0 ${svgWidth} ${svgHeight}`}
      >
        {/* pathA: 隨機路徑 1 */}
        <path
          ref={pathA}
          d={randomPathA}
          fill="none"
          stroke="#ccc"
          strokeWidth="4"
          strokeDasharray="6 6"
        />
        {/* pathB: 隨機路徑 2 */}
        <path
          ref={pathB}
          d={randomPathB}
          fill="none"
          stroke="#ccc"
          strokeWidth="4"
          strokeDasharray="6 6"
        />
      </svg>

      {/* 小車 */}
      <motion.img
        src={carImg}
        alt="van"
        className="absolute w-16 h-16 pointer-events-none"
        animate={controls}
        transition={{ ease: "linear" }}
      />

      {/* Markers */}
      {locations.map((loc) => {
        // 取得該路徑終點
        const ref = loc.pathId === "pathA" ? pathA : pathB;
        // 檢查 ref.current 是否存在，因為在初次渲染時可能還未賦值
        const total = ref.current?.getTotalLength() || 0;
        const p = ref.current?.getPointAtLength(total) || { x: 0, y: 0 };
        return (
          <img
            key={loc.id}
            src={loc.markerImg}
            alt={`marker${loc.id}`}
            className="absolute w-8 h-8 cursor-pointer"
            style={{ left: p.x - 12, top: p.y - 12 }}
            onClick={() => onMarkerClick(loc)}
          />
        );
      })}

      {/* Modal */}
      <AnimatePresence>
        {showModal && currentLoc && (
          <motion.div
            className="fixed inset-0 bg-black/50 flex items-center justify-center z-50"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <motion.div
              className="relative bg-white rounded-2xl p-6 shadow-2xl max-w-sm w-full border-4 border-orange-400"
              initial={{ scaleX: 0 }}
              animate={{ scaleX: 1 }}
              exit={{ scaleX: 0 }}
              transition={{ duration: 0.4 }}
              style={{ transformOrigin: "center" }}
            >
              <button
                className="absolute top-3 right-3 text-2xl text-gray-500 hover:text-gray-800"
                onClick={() => setShowModal(false)}
              >
                &times;
              </button>
              <h2 className="text-2xl font-bold text-orange-600 mb-2">
                {currentLoc.modal.name}
              </h2>
              <p className="text-gray-700 mb-1">
                <span className="font-semibold">地址：</span>
                {currentLoc.modal.address}
              </p>
              <p className="text-gray-700">
                <span className="font-semibold">營業時間：</span>
                {currentLoc.modal.hours}
              </p>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
