import React, { useRef, useEffect } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import dudeImage from "../assets/images/van.webp";

gsap.registerPlugin(ScrollTrigger);

export default function MapScroll() {
  const containerRef = useRef(null);
  const contentRef = useRef(null);

  useEffect(() => {
    const ctx = gsap.context(() => {
      const content = contentRef.current;
      const totalWidth = content.scrollWidth - window.innerWidth;

      gsap.to(content, {
        x: () => `-${totalWidth}px`,
        ease: "none",
        scrollTrigger: {
          trigger: containerRef.current,
          start: "top top",
          end: () => `+=${totalWidth}`, // ‚Üê ÈóúÈçµÔºÅË¶ÅÁ≠â scroll ÂØ¨Â∫¶Ëµ∞ÂÆå
          scrub: true,
          pin: true,
          anticipatePin: 1,
          invalidateOnRefresh: true, // ‚Üê ‰øùÈö™Ôºåresize ÊôÇÈáçÊñ∞Ë®àÁÆó
        },
      });
    }, containerRef);

    return () => ctx.revert(); // ‚Üê Ê∏ÖÈô§ÂãïÁï´
  }, []);

  return (
    <section ref={containerRef} className="relative h-[100vh] overflow-hidden">
      {/* Âõ∫ÂÆöÂúñÁâá */}
      <img
        src={dudeImage}
        alt="fixed van"
        className="fixed bottom-6 left-6 w-40 h-auto z-10 pointer-events-none"
      />

      {/* Ê©´ÂêëÂÖßÂÆπÔºö‰∏ÄÂÆöË¶Å w-fit ËÆì scrollWidth Ê≠£Á¢∫ */}
      <div ref={contentRef} className="flex w-fit h-screen">
        {[
          { title: "Hello üëã", color: "#f97316" },
          { title: "ÊàëÊòØË™∞", color: "#10b981" },
          { title: "ÊàëÊúÉ‰ªÄÈ∫º", color: "#3b82f6" },
          { title: "ËÅØÁµ°Êàë", color: "#f43f5e" },
          { title: "Êñ∞Â∫óÈù¢Ë≥áË®ä", color: "#facc15", image: dudeImage },
        ].map((item, i) => (
          <section
            key={i}
            className="w-screen h-screen flex items-center justify-center p-10"
            style={{ backgroundColor: item.color }}
          >
            <div className="max-w-xl text-center text-white space-y-4">
              <h2 className="text-4xl font-bold">{item.title}</h2>
              {item.image && (
                <img
                  src={item.image}
                  alt="ÈôÑÂúñ"
                  className="mx-auto w-64 rounded-lg shadow-md"
                />
