import React, { useRef, useEffect } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import vanImage from "../assets/images/van.webp";
import shopImage from "../assets/images/shop.png";

gsap.registerPlugin(ScrollTrigger);

export default function MapScroll() {
  const containerRef = useRef(null);
  const contentRef = useRef(null);

  useEffect(() => {
    const ctx = gsap.context(() => {
      const content = contentRef.current;
      const totalScrollWidth = content.scrollWidth;

      gsap.to(content, {
        x: `-${totalScrollWidth}`,
        ease: "none",
        scrollTrigger: {
          trigger: containerRef.current,
          start: "top top",
          end: () => `+=${totalScrollWidth}`,
          scrub: true,
          pin: true,
          pinSpacer: false,
          anticipatePin: 1,
          invalidateOnRefresh: true,
          onUpdate: (self) => {
            console.log("scroll progress", self.progress);
          },
        },
      });

      ScrollTrigger.refresh();
    }, containerRef);

    return () => ctx.revert();
  }, []);

  return (
    <section
      ref={containerRef}
      className="relative overflow-hidden h-[100vh]" // ← 要有明確高度
    >
      {/* 固定圖片 */}
      <img
        src={vanImage}
        alt="van"
        className="fixed bottom-6 left-6 w-40 z-10 pointer-events-none"
      />

      {/* 可橫向滾動的內容 */}
      <div
        ref={contentRef}
        className="flex h-screen w-fit bg-slate-400" // ← flex + w-fit 是重點
      >
        {[
          { title: "nunu Shop", image: shopImage },
          { title: "nunu Van", image: vanImage },
        ].map((item, i) => (
          <section
            key={i}
            className="w-screen h-screen flex items-center justify-center p-10"
            style={{ backgroundColor: item.color }}
          >
            <div className="max-w-xl text-center space-y-4">
              <h2 className="text-4xl font-bold">{item.title}</h2>
              {item.image && (
                <img
                  src={item.image}
                  alt="nunu"
                  className="mx-auto w-64 rounded-lg shadow-md"
                />
              )}
              <p className="text-lg">這裡可以放描述文字或商店資訊。</p>
            </div>
          </section>
        ))}
      </div>
    </section>
  );
}
