import React, { useRef, useState, useEffect } from "react";
import { motion, useAnimation, AnimatePresence } from "framer-motion";
import van1 from "../assets/images/van1.webp"; // 只需導入 van1，移除 van2
import marker1 from "../assets/images/cake/mainImage.webp";
import marker2 from "../assets/images/cake/mainImage.webp"; // 重新導入 marker2

// 移除不使用的輔助函數和隨機路徑生成邏輯

export default function MapScene() {
  // 設定 SVG 的寬高和 viewBox 為你指定的值
  const svgWidth = 1280;
  const svgHeight = 425;

  // 現在只有一個 path 的參照 (ref)
  const mainPathRef = useRef(null);
  const controls = useAnimation();

  const [currentLoc, setCurrentLoc] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isMoving, setIsMoving] = useState(false);
  const [carPos, setCarPos] = useState({
    x: 0,
    y: `- ${(svgHeight / svgWidth) * 100}%`,
  });
  const [carImg, setCarImg] = useState(van1); // 預設只使用 van1

  // Locations 陣列現在有兩個地點，都與同一個路徑相關，但位置不同
  const locations = [
    {
      id: 1,
      name: "nunu Shop - 起點", // 第一個地點代表路徑起點
      address: "起點地址：新加坡某處",
      hours: "Mon - Sun | 10:00 - 18:00",
      markerImg: marker1,
      carImg: van1, // 統一使用 van1
      pathPosition: 0, // 表示路徑長度的 0% (起點)
    },
    {
      id: 2,
      name: "nunu Van - 終點", // 第二個地點代表路徑終點
      address: "終點地址：新加坡其他處",
      hours: "Weekend | 11:00 - 18:00",
      markerImg: marker2,
      carImg: van1, // 統一使用 van1
      pathPosition: 1, // 表示路徑長度的 100% (終點)
    },
  ];

  // 在組件載入時，將車子定位在主要路徑的起點
  useEffect(() => {
    // 確保 mainPathRef.current 已經被賦值 (即 SVG 路徑已經渲染)
    if (mainPathRef.current) {
      const p = mainPathRef.current.getPointAtLength(0); // 獲取路徑起點座標
      // 調整車子位置使其中心點對齊路徑起點
      // 車子圖片是 w-16 h-16 (即 64x64px)，所以其中心點位於 (x+32, y+32)
      // 因此，我們需要將其左上角定位在 (x-32, y-32)
      setCarPos({ x: p.x - 32, y: p.y - 32 });
    }
  }, []); // 空陣列表示只在組件掛載時執行一次

  // 將車子位置綁定到 Framer Motion 的 controls，以控制動畫
  useEffect(() => {
    controls.set({ x: carPos.x, y: carPos.y });
  }, [carPos, controls]);

  // 控制車子沿路徑行駛的動畫邏輯
  const moveAlong = async (pathRef) => {
    setIsMoving(true); // 設定移動狀態為 true
    const pathEl = pathRef.current;
    if (!pathEl) {
      // 安全檢查，確保路徑元素存在
      setIsMoving(false);
      return;
    }
    const totalLength = pathEl.getTotalLength(); // 獲取路徑的總長度

    await new Promise((resolve) => {
      let startTime = null; // 動畫開始時間
      const duration = 2000; // 動畫持續時間 (毫秒)

      const animate = (timestamp) => {
        if (!startTime) startTime = timestamp; // 初始化開始時間
        const elapsed = timestamp - startTime; // 經過的時間
        const progress = Math.min(elapsed / duration, 1); // 進度變數 (0 到 1)

        // 獲取當前進度下的路徑點座標
        const point = pathEl.getPointAtLength(totalLength * progress);
        // 更新車子位置 (置中)
        setCarPos({ x: point.x - 32, y: point.y - 32 });

        if (progress < 1) {
          requestAnimationFrame(animate); // 如果動畫未完成，繼續下一幀
        } else {
          resolve(); // 動畫完成，解決 Promise
        }
      };
      requestAnimationFrame(animate); // 開始動畫循環
    });

    // 動畫完成後，延遲 1 秒顯示 Modal 並重置移動狀態
    setTimeout(() => {
      setShowModal(true);
      setIsMoving(false);
    }, 1000);
  };

  // 點擊標記點時觸發的函式
  const onMarkerClick = (loc) => {
    if (isMoving) return; // 如果車子正在移動，則不響應點擊
    setShowModal(false); // 隱藏 Modal
    setCurrentLoc(loc); // 設定當前選中的地點
    setCarImg(loc.carImg); // 設定車子圖片 (這裡始終是 van1)

    // 由於現在只有一條主路徑，直接傳入 mainPathRef
    moveAlong(mainPathRef); // 觸發車子沿選定路徑行駛
  };

  return (
    // 最外層 div 容器的樣式，用於響應式顯示
    <div
      className="relative w-full overflow-hidden"
      // 透過設定 height: 0 和 padding-top 來維持 SVG 的長寬比
      // padding-top 的百分比是根據 svgHeight / svgWidth 的比例計算而來，確保不同螢幕尺寸下的顯示比例正確
      style={{ height: 0, paddingTop: `${(svgHeight / svgWidth) * 200}vh` }}
    >
      <svg
        className="absolute w-full h-full top-0 left-0" // 確保 SVG 填滿父容器並定位在左上角
        viewBox={`0 0 ${svgWidth} ${svgHeight}`} // 設定 SVG 的視圖框
      >
        {/* 單一主要路徑：使用您提供的固定 d 屬性 */}
        <path
          ref={mainPathRef} // 將路徑參照綁定到 mainPathRef
          d="M-2 78.9951C-2 78.9951 490.5 -76.9943 551 63C611.5 202.994 370.5 292.5 530.5 389.5C690.5 486.5 1277.5 324 1277.5 324"
          fill="none" // 不填充顏色
          stroke="#3B3B3B" // 路徑顏色
          strokeOpacity="0.63" // 透明度
          strokeWidth="8" // 線條寬度
          strokeLinecap="round" // 線段末端形狀
          strokeLinejoin="round" // 線段連接處形狀
          strokeDasharray="23 23" // 虛線模式 (實線23，間隔23)
        />
        {/* 移除了 pathB */}
      </svg>

      {/* 小車圖片的 Framer Motion 動畫容器 */}
      <motion.img
        src={carImg} // 這裡只會是 van1
        alt="van"
        className="absolute w-28 h-28" // 固定車子尺寸 (64x64px)
        animate={controls} // 綁定動畫控制
        transition={{ ease: "linear", duration: 2 }} // 動畫過渡效果
      />

      {/* 遍歷 locations 陣列，渲染每個標記點 */}
      {locations.map((loc) => {
        // 所有標記點都使用 mainPathRef
        const ref = mainPathRef;
        // 獲取路徑的總長度，如果 ref.current 還不存在則為 0
        const totalLength = ref.current?.getTotalLength() || 0;
        // 根據 loc.pathPosition (0 或 1) 獲取路徑上對應的點座標
        const p = ref.current?.getPointAtLength(
          totalLength * loc.pathPosition
        ) || { x: 0, y: 0 };
        return (
          <img
            key={loc.id} // 唯一的 key
            src={loc.markerImg} // 標記點圖片 (marker1 或 marker2)
            alt={`marker-${loc.id}`} // 圖片替代文字
            className="absolute w-8 h-8 cursor-pointer" // 標記點尺寸和鼠標樣式 (32x32px)
            style={{ left: p.x - 12, top: p.y - 12 }} // 標記點置中 (32/2 = 16，但考慮到圖片邊緣，這裡使用 12 來微調)
            onClick={() => onMarkerClick(loc)} // 點擊事件
          />
        );
      })}

      {/* Modal 顯示邏輯 (使用 AnimatePresence 處理進場/離場動畫) */}
      <AnimatePresence>
        {showModal && currentLoc && (
          <motion.div
            className="fixed inset-0 bg-black/50 flex items-center justify-center z-50"
            initial={{ opacity: 0 }} // 初始透明度為 0
            animate={{ opacity: 1 }} // 動畫到透明度 1
            exit={{ opacity: 0 }} // 離場時動畫到透明度 0
          >
            <motion.div
              className="relative bg-white rounded-2xl p-6 shadow-2xl max-w-sm w-full border-4 border-orange-400"
              initial={{ scaleX: 0 }} // 初始 X 軸縮放為 0
              animate={{ scaleX: 1 }} // 動畫到 X 軸縮放 1
              exit={{ scaleX: 0 }} // 離場時動畫到 X 軸縮放 0
              transition={{ duration: 0.4 }} // 過渡時間 0.4 秒
              style={{ transformOrigin: "center" }} // 變形原點為中心
            >
              <button
                className="absolute top-3 right-3 text-2xl text-gray-500 hover:text-gray-800"
                onClick={() => setShowModal(false)} // 點擊關閉 Modal
              >
                &times; {/* 關閉按鈕的叉號 */}
              </button>
              <h2 className="text-2xl font-bold text-orange-600 mb-2">
                {currentLoc.name} {/* 修正這裡：直接存取 name */}
              </h2>
              <p className="text-gray-700 mb-1">
                <span className="font-semibold">地址：</span>
                {currentLoc.address} {/* 修正這裡：直接存取 address */}
              </p>
              <p className="text-gray-700">
                <span className="font-semibold">營業時間：</span>
                {currentLoc.hours} {/* 修正這裡：直接存取 hours */}
              </p>
            </motion.div>
