import React, { useState, useEffect, useRef } from "react";

export default function Component() {
  const [scrolledPast, setScrolledPast] = useState(false);
  const firstSectionRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (!firstSectionRef.current) return;
      const firstSectionHeight = firstSectionRef.current.offsetHeight;
      const scrollY = window.scrollY || window.pageYOffset;

      if (scrollY >= firstSectionHeight) {
        setScrolledPast(true);
      } else {
        setScrolledPast(false);
      }
    };

    window.addEventListener("scroll", handleScroll);

    // 元件卸載時移除監聽器
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
      {/* 第一區塊，這裡放在 component 裡，也可以調整高度 */}
      <div
        ref={firstSectionRef}
        style={{ height: "100vh", backgroundColor: "#eee" }}
      >
        第一區塊（滾動查看效果）
      </div>

      {/* 你的目標區塊 */}
      <div>
        <div className={`component-svg z-10 ${scrolledPast ? "top" : ""}`}>
          <div>{scrolledPast ? "Top" : "Scroll"}</div>
        </div>
      </div>
    </>
  );
}
