import React, { useRef, useState, useEffect } from "react";
import { motion, useAnimation, AnimatePresence } from "framer-motion";
import van1 from "../assets/images/van1.webp"; // 只需要 van1
import marker1 from "../assets/images/cake/mainImage.webp"; // 只需一個 marker

// 移除不使用的 generateSquaredPath 函數，因為現在路徑是固定的

export default function MapScene() {
  // 將 SVG 的 viewbox 設為你指定的值
  const svgWidth = 1280;
  const svgHeight = 425;

  // 只需要一個 path 的 ref
  const pathRef = useRef(null);
  const controls = useAnimation();

  const [currentLoc, setCurrentLoc] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isMoving, setIsMoving] = useState(false);
  const [carPos, setCarPos] = useState({ x: 0, y: 0 });
  const [carImg, setCarImg] = useState(van1); // 只需要一個車子圖片

  // locations 陣列也只需要一個地點
  const locations = [
    {
      id: 1,
      pathId: "pathMain", // 這裡使用一個新的 ID
      markerImg: marker1,
      modal: {
        name: "nunu Shop",
        address: "一個固定的地址",
        hours: "Mon - Sun | 10:00 - 18:00",
      },
      carImg: van1,
    },
  ];

  // on load，將車子定位在唯一的路徑起點
  useEffect(() => {
    // 確保路徑元素已載入
    if (pathRef.current) {
      const p = pathRef.current.getPointAtLength(0);
      setCarPos({ x: p.x, y: p.y });
    }
  }, []);

  // 將車子位置綁到 controls
  useEffect(() => {
    // 讓車子圖片中心點對齊路徑點
    // 假設車子圖片是 64x64px (w-16 h-16)，需減去 32px
    controls.set({ x: carPos.x - 32, y: carPos.y - 32 });
  }, [carPos, controls]);

  const moveAlong = async (pathRef) => {
    setIsMoving(true);
    const pathEl = pathRef.current;
    if (!pathEl) {
      setIsMoving(false);
      return;
    }
    const total = pathEl.getTotalLength();

    await new Promise((res) => {
      let t = 0;
      const step = () => {
        t += 0.005; // 調整移動速度
        if (t >= 1) t = 1;
        const p = pathEl.getPointAtLength(total * t);
        setCarPos({ x: p.x, y: p.y }); // 更新車子位置
        if (t < 1) {
          requestAnimationFrame(step);
        } else {
          res();
        }
      };
      requestAnimationFrame(step);
    });

    setTimeout(() => {
      setShowModal(true);
      setIsMoving(false);
    }, 1000);
  };

  const onMarkerClick = (loc) => {
    if (isMoving) return;
    setShowModal(false);
    setCurrentLoc(loc);
    setCarImg(loc.carImg);

    // 只傳入一個路徑 ref
    moveAlong(pathRef);
  };

  return (
    <div
      className="relative w-full h-[425px] verflow-hidden "
      style={{ height: `${svgHeight}px` }}
    >
      <svg
        className="absolute w-full h-full"
        viewBox={`0 0 ${svgWidth} ${svgHeight}`}
      >
        {/* 只保留一個 path，並使用你提供的 d 屬性 */}
        <path
          ref={pathRef}
          d="M-2 78.9951C-2 78.9951 490.5 -76.9943 551 63C611.5 202.994 370.5 292.5 530.5 389.5C690.5 486.5 1277.5 324 1277.5 324"
          fill="none"
          stroke="#ccc"
          strokeWidth="4"
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeDasharray="6 6" // 為了和你的範例一致，可以改為 23 23，或保留 6 6
        />
      </svg>

      {/* 小車，使用單一圖片 */}
      <motion.img
        src={carImg}
        alt="van"
        className="absolute w-16 h-16" // 移除 pointer-events-none，因為車子沒有互動
        animate={controls}
        transition={{ ease: "linear" }}
      />

      {/* Markers，現在只會渲染一個 */}
      {locations.map((loc) => {
        // 現在只有一個 pathRef
        const ref = pathRef;
        const total = ref.current?.getTotalLength() || 0;
        const p = ref.current?.getPointAtLength(total) || { x: 0, y: 0 };
        return (
          <img
            key={loc.id}
            src={loc.markerImg}
            alt={`marker${loc.id}`}
            className="absolute w-8 h-8 cursor-pointer"
            style={{ left: p.x - 12, top: p.y - 12 }}
            onClick={() => onMarkerClick(loc)}
          />
        );
      })}

      {/* Modal */}
      <AnimatePresence>
        {showModal && currentLoc && (
          <motion.div
            className="fixed inset-0 bg-black/50 flex items-center justify-center z-50"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <motion.div
              className="relative bg-white rounded-2xl p-6 shadow-2xl max-w-sm w-full border-4 border-orange-400"
              initial={{ scaleX: 0 }}
              animate={{ scaleX: 1 }}
              exit={{ scaleX: 0 }}
              transition={{ duration: 0.4 }}
              style={{ transformOrigin: "center" }}
            >
              <button
                className="absolute top-3 right-3 text-2xl text-gray-500 hover:text-gray-800"
                onClick={() => setShowModal(false)}
              >
                &times;
              </button>
              <h2 className="text-2xl font-bold text-orange-600 mb-2">
                {currentLoc.modal.name}
              </h2>
              <p className="text-gray-700 mb-1">
                <span className="font-semibold">地址：</span>
                {currentLoc.modal.address}
              </p>
              <p className="text-gray-700">
                <span className="font-semibold">營業時間：</span>
                {currentLoc.modal.hours}
              </p>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
