import React, { useRef, useEffect } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import vanImage from "../assets/images/van.webp";
import shopImage from "../assets/images/shop.png";

gsap.registerPlugin(ScrollTrigger);

export default function MapScroll() {
  const containerRef = useRef(null);
  const contentRef = useRef(null);

  useEffect(() => {
    const ctx = gsap.context(() => {
      const content = contentRef.current;
      const scrollAmount = content.scrollWidth - window.innerWidth;

      gsap.to(content, {
        x: () => `-${content.scrollWidth - window.innerWidth}px`,
        ease: "none",
        scrollTrigger: {
          trigger: containerRef.current,
          start: "top top",
          end: () => `+=${scrollAmount / 30}vh`,
          scrub: true,
          pin: true,
          anticipatePin: 0,
          pinType: "transform",
          invalidateOnRefresh: true,
          onUpdate: (self) => {
            console.log("scroll progress", self.progress);
          },
        },
      });

      ScrollTrigger.refresh();
    }, containerRef);

    return () => ctx.revert();
  }, []);

  return (
    <section
      ref={containerRef}
      className="relative overflow-hidden h-[70vh]" // ← 要有明確高度
    >
      {/* 固定圖片
      <img
        src={vanImage}
        alt="van"
        className="fixed bottom-6 left-6 w-64 aspect-auto z-10 pointer-events-none"
      /> */}

      {/* 可橫向滾動的內容 */}
      <div
        ref={contentRef}
        className="flex h-full w-fit will-change-transform" // ← flex + w-fit 是重點
      >
        {[
          {
            title: "nunu Shop",
            content: "266 South Bridge Rd, #000, Singapore",
            week: "Mon - Sun |",
            time: "10:00 - 18:00",
            image: shopImage,
          },
          {
            title: "nunu Van",
            content: "Suntec City 3 Temasek Blvd, Singapore 038983",
            week: "Weekend |",
            time: "11:00 - 18:00",
            image: vanImage,
          },
        ].map((item, i) => (
          <section
            key={i}
            className="bg-yellow-200 w-screen h-full flex flex-col items-center justify-center p-4 text-dark text-center"
          >
            {item.image && (
              <img
                src={item.image}
                alt="nunu"
                className="mx-auto w-72 mb-6 h-[300px]"
              />
            )}
            <div className="flex flex-col items-center gap-2">
              <h4 className="text-2xl font-bold after:content'' after:bg-dark after:w-11/12 after:h-0.5 after:mt-1 after:block">
                {item.title}
              </h4>
              <div className="text-sm">{item.content}</div>
              <div>
                <div className="text-sm font-semibold inline-block">
                  {item.week}
                </div>
                <div className="text-sm  inline-block ml-2">{item.time}</div>
              </div>
            </div>
          </section>
        ))}
      </div>
    </section>
  );
}
