import React, { useRef, useState, useEffect } from "react";
import { motion, useAnimation, AnimatePresence } from "framer-motion";
import van1 from "../assets/images/van1.webp"; // 只需導入 van1
import marker1 from "../assets/images/cake/mainImage.webp"; // 只需導入 marker1

// 移除 getRandomInt 和 generateSquaredPath 函數，因為路徑現在是固定的

export default function MapScene() {
  // 設定 SVG 的寬高和 viewBox 為你指定的值
  const svgWidth = 1280;
  const svgHeight = 425;

  // 只需要一個 path 的參照 (ref)
  const mainPathRef = useRef(null);
  const controls = useAnimation();

  const [currentLoc, setCurrentLoc] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [isMoving, setIsMoving] = useState(false);
  const [carPos, setCarPos] = useState({ x: 0, y: 0 });
  const [carImg, setCarImg] = useState(van1); // 預設只使用 van1

  // Locations 陣列現在只包含一個地點，因為我們只有一條路徑
  const locations = [
    {
      id: 1,
      pathId: "mainPath", // 路徑 ID
      markerImg: marker1,
      modal: {
        name: "nunu Shop",
        address: "固定地址：新加坡某處",
        hours: "Mon - Sun | 10:00 - 18:00",
      },
      carImg: van1, // 只有一台車的圖片
    },
  ];

  // 在組件載入時，將車子定位在主要路徑的起點
  useEffect(() => {
    // 確保 mainPathRef.current 已經被賦值
    if (mainPathRef.current) {
      const p = mainPathRef.current.getPointAtLength(0);
      // 調整車子位置使其中心點對齊路徑點 (車子是 w-16 h-16 = 64x64px，所以減去 32)
      setCarPos({ x: p.x - 32, y: p.y - 32 });
    }
  }, []); // 空陣列表示只在組件掛載時執行一次

  // 將車子位置綁定到 Framer Motion 的 controls，以控制動畫
  useEffect(() => {
    controls.set({ x: carPos.x, y: carPos.y });
  }, [carPos, controls]);

  // 控制車子沿路徑行駛的動畫邏輯
  const moveAlong = async (pathRef) => {
    setIsMoving(true); // 設定移動狀態為 true
    const pathEl = pathRef.current;
    if (!pathEl) {
      // 安全檢查，確保路徑元素存在
      setIsMoving(false);
      return;
    }
    const totalLength = pathEl.getTotalLength(); // 獲取路徑的總長度

    await new Promise((resolve) => {
      let t = 0; // 進度變數 (0 到 1)
      const duration = 2000; // 動畫持續時間 (毫秒)

      const animate = (timestamp) => {
        const progress = Math.min(
          (timestamp - (animate.startTime || timestamp)) / duration,
          1
        );
        if (!animate.startTime) animate.startTime = timestamp; // 初始化開始時間

        const point = pathEl.getPointAtLength(totalLength * progress);
        setCarPos({ x: point.x - 32, y: point.y - 32 }); // 更新車子位置 (置中)

        if (progress < 1) {
          requestAnimationFrame(animate); // 繼續動畫
        } else {
          resolve(); // 動畫完成，解決 Promise
        }
      };
      requestAnimationFrame(animate); // 開始動畫循環
    });

    // 動畫完成後，延遲 1 秒顯示 Modal 並重置移動狀態
    setTimeout(() => {
      setShowModal(true);
      setIsMoving(false);
    }, 1000);
  };

  // 點擊標記點時觸發的函式
  const onMarkerClick = (loc) => {
    if (isMoving) return; // 如果車子正在移動，則不響應點擊
    setShowModal(false); // 隱藏 Modal
    setCurrentLoc(loc); // 設定當前選中的地點
    setCarImg(loc.carImg); // 設定車子圖片 (這裡始終是 van1)

    // 觸發車子沿主要路徑行駛
    moveAlong(mainPathRef);
  };

  return (
    // 調整最外層 div 的樣式，使其在小螢幕上也能正確顯示
    <div
      className="relative w-full overflow-hidden rounded-xl border"
      // 移除固定的 width 和 height，改用 padding-bottom 來維持 SVG 的長寬比
      // height: 0 是為了讓 padding-bottom 完全控制高度
      style={{ height: 0, paddingTop: `${(svgHeight / svgWidth) * 100}%` }}
    >
      <svg
        className="absolute w-full h-full top-0 left-0" // 確保 SVG 填滿父容器
        viewBox={`0 0 ${svgWidth} ${svgHeight}`}
      >
        {/* 主要路徑：使用你提供的固定 d 屬性 */}
        <path
          ref={mainPathRef}
          d="M-2 78.9951C-2 78.9951 490.5 -76.9943 551 63C611.5 202.994 370.5 292.5 530.5 389.5C690.5 486.5 1277.5 324 1277.5 324"
          fill="none"
          stroke="#3B3B3B" // 路徑顏色
          strokeOpacity="0.63" // 透明度
          strokeWidth="8" // 線條寬度
          strokeLinecap="round" // 線段末端形狀
          strokeLinejoin="round" // 線段連接處形狀
          strokeDasharray="23 23" // 虛線模式
        />
      </svg>

      {/* 小車圖片 */}
      <motion.img
        src={carImg} // 這裡只會是 van1
        alt="van"
        className="absolute w-16 h-16" // 固定車子尺寸
        animate={controls} // 綁定動畫控制
        transition={{ ease: "linear", duration: 2 }} // 動畫過渡效果
      />

      {/* 標記點，現在只有一個 */}
      {locations.map((loc) => {
        // 獲取主要路徑的終點座標
        const ref = mainPathRef;
        const totalLength = ref.current?.getTotalLength() || 0;
        const p = ref.current?.getPointAtLength(totalLength) || { x: 0, y: 0 };
        return (
          <img
            key={loc.id}
            src={loc.markerImg} // 這裡只會是 marker1
            alt={`marker-${loc.id}`}
            className="absolute w-8 h-8 cursor-pointer" // 標記點尺寸
            style={{ left: p.x - 12, top: p.y - 12 }} // 標記點置中
            onClick={() => onMarkerClick(loc)} // 點擊事件
          />
        );
      })}

      {/* Modal 顯示邏輯 */}
      <AnimatePresence>
        {showModal && currentLoc && (
          <motion.div
            className="fixed inset-0 bg-black/50 flex items-center justify-center z-50"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <motion.div
              className="relative bg-white rounded-2xl p-6 shadow-2xl max-w-sm w-full border-4 border-orange-400"
              initial={{ scaleX: 0 }}
              animate={{ scaleX: 1 }}
              exit={{ scaleX: 0 }}
              transition={{ duration: 0.4 }}
              style={{ transformOrigin: "center" }}
            >
              <button
                className="absolute top-3 right-3 text-2xl text-gray-500 hover:text-gray-800"
                onClick={() => setShowModal(false)}
              >
                &times;
              </button>
              <h2 className="text-2xl font-bold text-orange-600 mb-2">
                {currentLoc.modal.name}
              </h2>
              <p className="text-gray-700 mb-1">
                <span className="font-semibold">地址：</span>
                {currentLoc.modal.address}
              </p>
              <p className="text-gray-700">
                <span className="font-semibold">營業時間：</span>
                {currentLoc.modal.hours}
              </p>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
