import React, { useRef, useEffect } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import dudeImage from "../assets/images/van.webp";

gsap.registerPlugin(ScrollTrigger);

export default function MapScroll() {
  const containerRef = useRef();
  const contentRef = useRef();

  useEffect(() => {
    const sections = gsap.utils.toArray(".content-section");
    const totalWidth = contentRef.current.scrollWidth - window.innerWidth;

    gsap.to(contentRef.current, {
      x: () => `-${totalWidth}`,
      ease: "none",
      scrollTrigger: {
        trigger: ".trigger",
        start: "top center",
        end: "+=300",
        scrub: true,
        pin: true,
        anticipatePin: 1,
      },
    });

    ScrollTrigger.refresh();
  }, []);

  return (
    <div
      ref={containerRef}
      className="relative max-h-60 h-full w-full overflow-hidden"
    >
      {/* ‰∫∫Áâ©ÂúñÔºàÂõ∫ÂÆöÂ∑¶‰∏ãËßíÔºâ */}
      <img
        src={dudeImage}
        alt="walking dude"
        className="fixed bottom-0 left-10 w-56 h-auto z-10"
      />

      {/* Ê©´ÂêëÂÖßÂÆπÂçÄÂ°ä */}
      <div ref={contentRef} className="flex w-[400vw] h-full items-center">
        {["Hello üëã", "ÊàëÊòØË™∞", "ÊàëÊúÉ‰ªÄÈ∫º", "ËÅØÁµ°Êàë"].map((text, i) => (
          <section
            key={i}
            className="content-section w-screen h-full flex items-center justify-center text-4xl font-bold text-white"
            style={{
              backgroundColor: ["#f97316", "#10b981", "#3b82f6", "#f43f5e"][i],
            }}
          >
            {text}
          </section>
        ))}
      </div>
    </div>
  );
}
