import React, { useState, useEffect, useRef } from "react";

function Component() {
  const [scrolledPast, setScrolledPast] = useState(false);
  const [visible, setVisible] = useState(true);
  const [text, setText] = useState("Scroll");
  const timeoutRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY || window.pageYOffset;
      const triggerHeight = 500;

      if (scrollY >= triggerHeight && !scrolledPast) {
        fadeOutAndChangeText("Top");
        setScrolledPast(true);
      } else if (scrollY < triggerHeight && scrolledPast) {
        fadeOutAndChangeText("Scroll");
        setScrolledPast(false);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
      if (timeoutRef.current) clearTimeout(timeoutRef.current);
    };
  }, [scrolledPast]);

  function fadeOutAndChangeText(newText) {
    setVisible(false);

    timeoutRef.current = setTimeout(() => {
      setText(newText);
      setVisible(true);
    }, 300);
  }

  return (
    <div>
      <div
        className={`component-svg z-10`}
        style={{
          opacity: visible ? 1 : 0,
          transition: "opacity 0.3s ease",
        }}
      >
        <div>{text}</div>
      </div>
    </div>
  );
}

export default Component;
