import React, { useState } from "react";
import oreocake from "../assets/images/cake/oreo.png";
import oreocakeTitle from "../assets/images/cake/oreo-title.png";
import tarocake from "../assets/images/cake/taro.png";
import tarocakeTitle from "../assets/images/cake/taro-title.png";
import redbeancake from "../assets/images/cake/redbean.png";
import redbeancakeTitle from "../assets/images/cake/redbean-title.png";
import vanillacake from "../assets/images/cake/vanilla.png";
import vanillacakeTitle from "../assets/images/cake/vanilla-title.png";

const tabs = [
  {
    id: "tab1",
    labelImage: oreocake,
    labelTitleImage: oreocakeTitle,
    titleText: "oreo cake",
    content: {
      image: oreocake,
      textList: [
        "Crunchy Oreo cookies paired with rich cream, sweet but not overwhelming—every bite feels like dancing on clouds!",
      ],
    },
  },
  {
    id: "tab2",
    labelImage: tarocake,
    labelTitleImage: tarocakeTitle,
    titleText: "taro cake",
    content: {
      image: tarocake,
      textList: [
        "Smooth and sweet taro filling melts softly in your mouth, making you fall in love instantly !",
      ],
    },
  },
  {
    id: "tab3",
    labelImage: redbeancake,
    labelTitleImage: redbeancakeTitle,
    titleText: "red bean cake",
    content: {
      image: redbeancake,
      textList: [
        "Red bean is the original and traditional wheelcake filling. Made with care and love, the beans are cooked into a dense, sweet paste (almost like sweet potato) that can be enjoyed any time of the year.",
      ],
    },
  },
  {
    id: "tab4",
    labelImage: vanillacake,
    labelTitleImage: vanillacakeTitle,
    titleText: "Vanilla cake",
    content: {
      image: vanillacake,
      textList: [
        "One of the most classic flavours: made using English custard methods to produce a smooth rich custard filling with vanilla undertones.",
      ],
    },
  },
];

const Tabs = () => {
  const [activeTab, setActiveTab] = useState(tabs[0].id);
  const currentContent = tabs.find((tab) => tab.id === activeTab)?.content;

  return (
    <div className="relative text-center px-[40px] py-0 mb-[80px] md:px-[120px]">
      {currentContent && (
        <div
          key={activeTab}
          className="relative flex justify-center flex-col items-center bounce-in"
        >
          <img
            className="relative aspect-square w-96 h-fit md:w-72 xl:w-auto xl:max-w-[40rem] xl:left-10"
            src={currentContent.image}
            alt="nunu Wheel Cake"
          />
          <div className="relative text-left left-4 md:text-base xl:left-20">
            <div className="mb-6 text-lg font-semibold">
              {currentTab.titleText /* 這裡是標題文字 */}
            </div>

            {/* 文字內容 */}
            {currentContent.textList.map((item, index) => (
              <p key={index} className="relative mb-10">
                {item}
              </p>
            ))}
          </div>
        </div>
      )}
      {/* 標籤 */}
      <div className="flex justify-center items-center gap-12">
        {tabs.map((tab) => (
          <div
            className="flex flex-col justify-center items-center gap-2"
            key={tab.id}
            onClick={() => setActiveTab(tab.id)}
          >
            <img
              className="w-32 h-auto aspect-square"
              src={tab.labelImage}
              alt="nunu Wheel Cake"
            />
            <img
              className="labelTitle w-auto h-8"
              src={tab.labelTitleImage}
              alt="nunu Wheel Cake"
            />
            {tab.label}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tabs;
