import React, { useRef, useEffect } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import vanImage from "../assets/images/van.webp";
import shopImage from "../assets/images/shop.png";

gsap.registerPlugin(ScrollTrigger);

export default function MapScroll() {
  const containerRef = useRef(null);
  const contentRef = useRef(null);

  useEffect(() => {
    const ctx = gsap.context(() => {
      const content = contentRef.current;
      const scrollAmount = content.scrollWidth - window.innerWidth;

      gsap.to(content, {
        x: () => `-${content.scrollWidth - window.innerWidth}px`,
        ease: "none",
        scrollTrigger: {
          trigger: containerRef.current,
          start: "top top",
          end: () => `+${content.scrollWidth - window.innerWidth}px`,
          scrub: true,
          pin: true,
          anticipatePin: 0,
          pinType: "transform",
          invalidateOnRefresh: true,
          onUpdate: (self) => {
            console.log("scroll progress", self.progress);
          },
        },
      });

      ScrollTrigger.refresh();
    }, containerRef);

    return () => ctx.revert();
  }, []);

  return (
    <section
      ref={containerRef}
      className="relative overflow-hidden h-[100vh]" // ← 要有明確高度
    >
      {/* 固定圖片 */}
      <img
        src={vanImage}
        alt="van"
        className="fixed top-0 left-6 w-52 aspect-auto z-10 pointer-events-none"
      />
      {/* 可橫向滾動的內容 */}
      <div
        ref={contentRef}
        className="flex h-full w-fit will-change-transform" // ← flex + w-fit 是重點
      >
        {[
          {
            title: "nunu Shop",
            content: "266 South Bridge Rd, #000, Singapore",
            week: "Mon - Sun |",
            time: "10:00 - 18:00",
            image: shopImage,
          },
          {
            title: "nunu Van",
            content: "Suntec City 3 Temasek Blvd, Singapore 038983",
            week: "Weekend |",
            time: "11:00 - 18:00",
            image: vanImage,
          },
        ].map((item, i) => (
          <section
            key={i}
            className="w-screen h-full flex flex-col items-center p-4 pt-40 text-dark text-center"
          >
            {item.image && (
              <img
                src={item.image}
                alt="nunu"
                className="mx-auto w-72 mb-6 h-[300px]"
              />
            )}
            <div className="flex flex-col items-center gap-2">
              <h4 className="text-2xl font-bold after:content'' after:bg-dark after:w-11/12 after:h-0.5 after:mt-1 after:block">
                {item.title}
              </h4>
              <div className="text-sm">{item.content}</div>
              <div>
                <div className="text-sm font-semibold inline-block">
                  {item.week}
                </div>
                <div className="text-sm  inline-block ml-2">{item.time}</div>
              </div>
            </div>
          </section>
        ))}
        <svg
          className="absolute top-10 r-0 overflow-hidden -z-10"
          width="400"
          height="300"
          viewBox="0 0 400 300"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M-2 78.9951C-2 78.9951 490.5 -76.9943 551 63C611.5 202.994 370.5 292.5 530.5 389.5C690.5 486.5 1277.5 324 1277.5 324"
            stroke="#3B3B3B"
            stroke-opacity="0.63"
            stroke-width="8"
            stroke-linecap="round"
            stroke-linejoin="round"
            stroke-dasharray="23 23"
          />
        </svg>
      </div>
    </section>
  );
}
